"use client";

import { useEffect, useState, useRef } from "react";
import { useRouter } from "next/navigation";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import CompleteProfile from "../complete-profile";
import Image from "next/image";
import {
  ArrowRight,
  TrendingUp,
  Users,
  DollarSign,
  Share2,
  Calendar,
  Eye,
} from "lucide-react";

interface DashboardStats {
  totalCampaigns: number;
  activeCampaigns: number;
  totalDonations: number;
  totalDonors: number;
  totalChained: number;
  totalEarnings: number;
  recentDonations: Array<{
    id: string;
    amount: number;
    currency: string;
    message: string;
    donorName: string;
    campaignTitle: string;
    createdAt: string;
  }>;
}

interface Campaign {
  id: string;
  title: string;
  subtitle: string;
  goalAmount: number;
  currentAmount: number;
  currency: string;
  status: string;
  isActive: boolean;
  coverImageUrl: string;
  progressPercentage: number;
  donationCount: number;
  createdAt: string;
}

export default function DashboardPage() {
  const [showWelcome, setShowWelcome] = useState(false);
  const [showCompleteProfile, setShowCompleteProfile] = useState(false);
  const [profileChecked, setProfileChecked] = useState(false);
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [loading, setLoading] = useState(true);
  const router = useRouter();
  const formRef = useRef<HTMLFormElement>(
    null
  ) as React.RefObject<HTMLFormElement>;

  useEffect(() => {
    async function checkProfile() {
      try {
        const res = await fetch("/api/user/profile", { method: "GET" });
        const data = await res.json();
        if (
          data.success &&
          data.user &&
          !data.user.hasCompletedProfile &&
          !data.user.hasSeenWelcomeModal
        ) {
          setShowWelcome(true);
        }
      } catch {
        // fallback: show modal if error
        setShowWelcome(true);
      } finally {
        setProfileChecked(true);
      }
    }
    checkProfile();
  }, []);

  useEffect(() => {
    async function loadDashboardData() {
      if (!profileChecked) return;

      try {
        setLoading(true);

        // Load dashboard stats
        const statsRes = await fetch("/api/dashboard/stats");
        const statsData = await statsRes.json();
        if (statsData.success) {
          setStats(statsData.stats);
        }

        // Load user campaigns
        const campaignsRes = await fetch("/api/dashboard/campaigns");
        const campaignsData = await campaignsRes.json();
        if (campaignsData.success) {
          setCampaigns(campaignsData.campaigns);
        }
      } catch (error) {
        console.error("Error loading dashboard data:", error);
      } finally {
        setLoading(false);
      }
    }
    loadDashboardData();
  }, [profileChecked]);

  // When closing the welcome modal, mark as seen in backend
  const handleCloseWelcome = async () => {
    setShowWelcome(false);
    setShowCompleteProfile(true);
    try {
      await fetch("/api/user/profile", {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ hasSeenWelcomeModal: true }),
      });
    } catch {}
  };

  const formatCurrency = (amount: number, currency: string) => {
    const currencyMap: Record<string, string> = {
      // ISO Codes
      USD: "USD",
      GBP: "GBP",
      NGN: "NGN",
      EUR: "EUR",
      CAD: "CAD",

      // Names
      "BRITISH POUND": "GBP",
      "US DOLLAR": "USD",
      NAIRA: "NGN",
      EURO: "EUR",
      "CANADIAN DOLLAR": "CAD",
      POUND: "GBP",

      // Symbols
      "£": "GBP",
      $: "USD",
      "₦": "NGN",
      "€": "EUR",
      C$: "CAD",
    };

    const normalizedCurrency = (currency || "").trim().toUpperCase();

    const code = currencyMap[normalizedCurrency] || "USD";

    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: code,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  };

  return (
    <div className="w-full 2xl:container 2xl:mx-auto p-6 h-fit">
      {/* Welcome Modal */}
      <Dialog open={showWelcome} onOpenChange={setShowWelcome}>
        <DialogContent className='w-[80%] max-w-md md:max-w-xl bg-[url(${getImageUrl('piggy-bank.png')})] bg-cover h-[400px] md:h-[600px] px-4 md:px-10 bg-no-repeat rounded-none outline-none font-source'>
          <DialogHeader>
            <div className="h-[3px] w-full bg-[#2C2C2C] rounded-none overflow-hidden mb-4">
              <div
                className="h-full bg-white animate-grow"
                style={{
                  animation: "grow 3s linear forwards",
                }}
              ></div>
            </div>
            <DialogTitle className="flex gap-2 items-center">
              <Image
                src="/images/logo.svg"
                alt=""
                width={24}
                height={24}
                className="md:w-8 md:h-8"
              />
              <p className="font-semibold text-2xl md:text-4xl text-[#104901]">
                Welcome to Chainfundit
              </p>
            </DialogTitle>
          </DialogHeader>
          <DialogFooter className="flex items-end">
            <Button
              className="w-full h-14 md:h-[72px] font-sans font-semibold text-lg md:text-2xl flex justify-between items-center"
              onClick={handleCloseWelcome}
            >
              Complete your profile
              <ArrowRight />
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Complete Profile Modal */}
      <Dialog open={showCompleteProfile} onOpenChange={setShowCompleteProfile}>
        <DialogContent className="bg-[#F5F5F5] max-w-md md:max-w-xl rounded-none font-source">
          <DialogHeader>
            <DialogTitle className="font-source font-semibold text-left text-2xl md:text-4xl text-[#104901]">
              Complete Your Profile
            </DialogTitle>
            <p className="font-normal text-base md:text-xl text-[#104901] text-left text-wrap">
              Enter your name and choose an avatar so your friends can recognise
