              </Button>
            </div>
          </div>
          <div className="p-6">
            {campaigns.length > 0 ? (
              <div className="space-y-6">
                {campaigns.slice(0, 6).map((campaign) => (
                  <div
                    key={campaign.id}
                    className="border rounded-lg overflow-hidden hover:shadow-md transition-shadow"
                  >
                    <div className="relative h-48 bg-gray-200">
                      {campaign.coverImageUrl && (
                        <Image
